// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    goal: '1',
    activity_level: '1',
    workout_type: '1',
    experience_level: '1',
    equipment: '1',
    time_available: '',
  });

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:8000/generate_workout_plan', formData)
      .then((response) => {
        setWorkoutPlan(response.data);
        setError(null);
      })
      .catch((error) => {
        setError('An error occurred. Please try again.');
        console.error(error);
      });
  };

  return (
    <div>
      {!workoutPlan ? (
        <form onSubmit={handleSubmit}>
          {/* Input fields for formData with handleChange */}
          {/* Similar to the form fields in your index.html */}
          {/* For brevity, input fields are not fully listed here */}
          <button type="submit">Generate Workout Plan</button>
        </form>
      ) : (
        <div>
          <h1>Your Workout Plan</h1>
          {/* Display the workoutPlan */}
          <button onClick={() => setWorkoutPlan(null)}>Go Back</button>
        </div>
      )}
      {error && <p>{error}</p>}
    </div>
  );
};

export default WorkoutPlannerForm;
