// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    goal: '1',
    activity_level: '1',
    workout_type: '1',
    experience_level: '1',
    equipment: '1',
    time_available: '',
  });

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [adjustFormData, setAdjustFormData] = useState({
    day: '',
    diet: '',
    sleep_hours: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    axios.post('http://localhost:8000/generate_workout_plan', data, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        setLoading(false);
        console.log('DEBUG front-end response:', response.data);

        if (response.data.error) {
          // Server responded with an error message
          setError(response.data.error);
          setWorkoutPlan(null);
        } else {
          // The server returned the workout plan successfully
          setWorkoutPlan(response.data.plan);
          setError(null);
        }
      })
      .catch((error) => {
        setLoading(false);
        // Handle network errors, etc.
        if (error.response && error.response.data && error.response.data.error) {
          setError(error.response.data.error);
        } else {
          setError('An error occurred. Please try again.');
        }
        setWorkoutPlan(null);
      });
  };

  const handleAdjustChange = (e) => {
    setAdjustFormData({...adjustFormData, [e.target.name]: e.target.value});
  };

  const handleAdjustSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('day', adjustFormData.day);
    data.append('diet', adjustFormData.diet);
    data.append('sleep_hours', adjustFormData.sleep_hours);

    axios.post('http://localhost:8000/adjust_workout_plan', data)
      .then((response) => {
        if(response.data.error) {
          setError(response.data.error);
        } else {
          const updatedPlan = {...workoutPlan};
          updatedPlan[response.data.day] = response.data.adjusted_plan;
          setWorkoutPlan(updatedPlan);
          setError(null);
        }
      })
      .catch((error) => {
        console.error('Error adjusting plan:', error);
        setError('An error occurred adjusting plan.');
      });
  };

  return (
    <div>
      {(() => {
        if (!workoutPlan && !loading) {
          return (
            <form onSubmit={handleSubmit} style={{ padding: '1rem', maxWidth: '500px' }}>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Name:</label>
                <input
                  type="text"
                  name="name"
                  required
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Enter your name"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Age:</label>
                <input
                  type="number"
                  name="age"
                  required
                  value={formData.age}
                  onChange={handleChange}
                  placeholder="e.g. 30"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Gender (M/F):</label>
                <input
                  type="text"
                  name="gender"
                  required
                  value={formData.gender}
                  onChange={handleChange}
                  placeholder="M or F"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Height (cm):</label>
                <input
                  type="number"
                  name="height"
                  required
                  value={formData.height}
                  onChange={handleChange}
                  placeholder="e.g. 175"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Weight (kg):</label>
                <input
                  type="number"
                  name="weight"
                  required
                  value={formData.weight}
                  onChange={handleChange}
                  placeholder="e.g. 70"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Fitness Goal:</label>
                <select
                  name="goal"
                  required
                  value={formData.goal}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '0.5rem' }}
                >
                  <option value="1">Weight Loss</option>
                  <option value="2">Muscle Gain</option>
                  <option value="3">Maintenance</option>
                </select>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Activity Level:</label>
                <select
                  name="activity_level"
                  required
                  value={formData.activity_level}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '0.5rem' }}
                >
                  <option value="1">Sedentary</option>
                  <option value="2">Light Activity</option>
                  <option value="3">Moderate Activity</option>
                  <option value="4">Active</option>
                  <option value="5">Very Active</option>
                </select>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Preferred Workout Type:</label>
                <select
                  name="workout_type"
                  required
                  value={formData.workout_type}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '0.5rem' }}
                >
                  <option value="1">Cardio</option>
                  <option value="2">Strength Training</option>
                  <option value="3">Flexibility</option>
                  <option value="4">HIIT</option>
                  <option value="5">Mixed</option>
                </select>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Experience Level:</label>
                <select
                  name="experience_level"
                  required
                  value={formData.experience_level}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '0.5rem' }}
                >
                  <option value="1">Beginner</option>
                  <option value="2">Intermediate</option>
                  <option value="3">Advanced</option>
                </select>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Available Equipment:</label>
                <select
                  name="equipment"
                  required
                  value={formData.equipment}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '0.5rem' }}
                >
                  <option value="1">No Equipment</option>
                  <option value="2">Basic Equipment</option>
                  <option value="3">Full Gym Access</option>
                </select>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', fontWeight: 'bold' }}>Time Available (minutes):</label>
                <input
                  type="number"
                  name="time_available"
                  required
                  value={formData.time_available}
                  onChange={handleChange}
                  placeholder="e.g. 30"
                  style={{ width: '100%', padding: '0.5rem' }}
                />
              </div>

              <button type="submit" style={{ padding: '0.8rem 1.2rem' }}>Generate Workout Plan</button>
            </form>
          );
        }

        if (loading) {
          return <p>Loading...</p>;
        }

        if (workoutPlan) {
          return (
            <div>
              <h1>Your Workout Plan</h1>
              <div className="workout-plan-container">
                {Object.entries(workoutPlan).map(([day, exercises]) => (
                  <div key={day}>
                    <h2>{day}</h2>
                    <ul>
                      {exercises.map((ex, i) => (
                        <li key={i}>{ex}</li>
                      ))}
                    </ul>
                  </div>
                ))}
              </div>

              <h3>Adjust Todayâ€™s Plan</h3>
              <form onSubmit={handleAdjustSubmit}>
                <div>
                  <label>Which Day?:</label>
                  <input
                    type="text"
                    name="day"
                    value={adjustFormData.day}
                    onChange={handleAdjustChange}
                    placeholder="e.g. Monday"
                  />
                </div>
                <div>
                  <label>Diet Description:</label>
                  <input
                    type="text"
                    name="diet"
                    value={adjustFormData.diet}
                    onChange={handleAdjustChange}
                    placeholder="e.g. High Protein"
                  />
                </div>
                <div>
                  <label>Sleep Hours:</label>
                  <input
                    type="number"
                    name="sleep_hours"
                    value={adjustFormData.sleep_hours}
                    onChange={handleAdjustChange}
                  />
                </div>
                <button type="submit">Adjust Plan</button>
              </form>

              <button onClick={() => setWorkoutPlan(null)}>Go Back</button>
              {error && <p style={{ color: 'red' }}>{error}</p>}
            </div>
          );
        }
      })()}
      {error && !workoutPlan && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default WorkoutPlannerForm;
