// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    goal: '1',
    activity_level: '1',
    workout_type: '1',
    experience_level: '1',
    equipment: '1',
    time_available: '',
  });

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    axios.post('http://localhost:8000/generate_workout_plan', data, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        setLoading(false);

        setWorkoutPlan(response.data.plan || null);
        setError(null);
      })
      .catch((error) => {
        setLoading(false);
        setError('An error occurred. Please try again.');
        console.error(error);
      });
  };

  return (
    <div>
      {(() => {
        if (!workoutPlan && !loading) {
          return (
            <form onSubmit={handleSubmit}>
              <div>
                <label>姓名:</label>
                <input
                  type="text"
                  name="name"
                  required
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label>年龄:</label>
                <input
                  type="number"
                  name="age"
                  required
                  value={formData.age}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label>性别 (M/F):</label>
                <input
                  type="text"
                  name="gender"
                  required
                  value={formData.gender}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label>身高 (cm):</label>
                <input
                  type="number"
                  name="height"
                  required
                  value={formData.height}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label>体重 (kg):</label>
                <input
                  type="number"
                  name="weight"
                  required
                  value={formData.weight}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label>健身目标:</label>
                <select
                  name="goal"
                  required
                  value={formData.goal}
                  onChange={handleChange}
                >
                  <option value="1">Weight Loss</option>
                  <option value="2">Muscle Gain</option>
                  <option value="3">Maintenance</option>
                </select>
              </div>

              <div>
                <label>活动水平:</label>
                <select
                  name="activity_level"
                  required
                  value={formData.activity_level}
                  onChange={handleChange}
                >
                  <option value="1">Sedentary</option>
                  <option value="2">Light Activity</option>
                  <option value="3">Moderate Activity</option>
                  <option value="4">Active</option>
                  <option value="5">Very Active</option>
                </select>
              </div>

              <div>
                <label>偏好运动类型:</label>
                <select
                  name="workout_type"
                  required
                  value={formData.workout_type}
                  onChange={handleChange}
                >
                  <option value="1">Cardio</option>
                  <option value="2">Strength Training</option>
                  <option value="3">Flexibility</option>
                  <option value="4">HIIT</option>
                  <option value="5">Mixed</option>
                </select>
              </div>

              <div>
                <label>经验水平:</label>
                <select
                  name="experience_level"
                  required
                  value={formData.experience_level}
                  onChange={handleChange}
                >
                  <option value="1">Beginner</option>
                  <option value="2">Intermediate</option>
                  <option value="3">Advanced</option>
                </select>
              </div>

              <div>
                <label>可用器材条件:</label>
                <select
                  name="equipment"
                  required
                  value={formData.equipment}
                  onChange={handleChange}
                >
                  <option value="1">No Equipment</option>
                  <option value="2">Basic Equipment</option>
                  <option value="3">Full Gym Access</option>
                </select>
              </div>

              <div>
                <label>每日可用时长(分钟):</label>
                <input
                  type="number"
                  name="time_available"
                  required
                  value={formData.time_available}
                  onChange={handleChange}
                />
              </div>

              <button type="submit">Generate Workout Plan</button>
            </form>
          );
        }
        if (loading) {
          return <p>Loading...</p>;
        }
        return (
          <div>
            <h1>Your Workout Plan</h1>
            {Object.entries(workoutPlan).map(([day, exercises]) => (
              <div key={day} style={{ marginBottom: '1rem' }}>
                <h2>{day}</h2>
                <ul>
                  {exercises.map((ex, i) => <li key={i}>{ex}</li>)}
                </ul>
              </div>
            ))}
            <button onClick={() => setWorkoutPlan(null)}>Go Back</button>
          </div>
        );
      })()}

      {error && <p>{error}</p>}
    </div>
  );
};

export default WorkoutPlannerForm;
