// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    goal: '1',
    activity_level: '1',
    workout_type: '1',
    experience_level: '1',
    equipment: '1',
    time_available: '',
  });

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    axios.post('http://localhost:8000/generate_workout_plan', data, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        setLoading(false);

        setWorkoutPlan(response.data.plan || null);
        setError(null);
      })
      .catch((error) => {
        setLoading(false);
        setError('An error occurred. Please try again.');
        console.error(error);
      });
  };

  return (
    <div>
      {(() => {
        if (!workoutPlan && !loading) {
          return (
            <form onSubmit={handleSubmit}>
              {/* Input fields for formData with handleChange */}
              {/* Similar to the form fields in your index.html */}
              {/* For brevity, input fields are not fully listed here */}
              <button type="submit">Generate Workout Plan</button>
            </form>
          );
        }
        if (loading) {
          return <p>Loading...</p>;
        }
        return (
          <div>
            <h1>Your Workout Plan</h1>
            {Object.entries(workoutPlan).map(([day, exercises]) => (
              <div key={day} style={{ marginBottom: '1rem' }}>
                <h2>{day}</h2>
                <ul>
                  {exercises.map((ex, i) => <li key={i}>{ex}</li>)}
                </ul>
              </div>
            ))}
            <button onClick={() => setWorkoutPlan(null)}>Go Back</button>
          </div>
        );
      })()}

      {error && <p>{error}</p>}
    </div>
  );
};

export default WorkoutPlannerForm;
